class Post_tag_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get post_tag by id
     */
    function get_post_tag($id)
    {
        return $this->db->get_where('post_tag',array('id'=>$id))->row_array();
    }

    function get_post_tag_join_tag($post_id)
    {
        $user_id = user_info()['id'];

        return $this->db->select('*')
        ->from('post_tag')
        ->join('tag', 'tag.id = post_tag.tag_id', 'right')
        ->join('pengajar', 'pengajar.id_kelas = tag.id')
        ->order_by('tag.id', 'asc')
        ->group_by('tag.title')
        ->where('post_tag.post_id = '.$post_id.' OR post_tag.post_id IS NULL')
        ->get()->result_array();

        // return $this->db->last_query();
    }

    function get_post_tag_join_tag_by_post_id($post_id)
    {
        return $this->db->select('tag.title')
        ->from('post_tag')
        ->where('post_tag.post_id', $post_id)
        ->join('tag', 'post_tag.tag_id = tag.id')
        ->get()->result_array();

        // return $this->db->last_query();
    }
        
    /*
     * Get all post_tag
     */
    function get_all_post_tag()
    {
        $this->db->order_by('id', 'desc');
        return $this->db->get('post_tag')->result_array();
    }
        
    /*
     * function to add new post_tag
     */
    function add_post_tag($params)
    {
        $this->db->insert('post_tag',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update post_tag
     */
    function update_post_tag($id,$params)
    {
        $this->db->where('id',$id);
        return $this->db->update('post_tag',$params);
    }
    
    /*
     * function to delete post_tag
     */
    function delete_post_tag($id)
    {
        return $this->db->delete('post_tag',array('id'=>$id));
    }
}